mapper.update:
update t_electrician
sys_hash=#{sys_hash}, sys_is_delete=1
where electrician_id=#{electrician_id}

mapper.select:
select e.*,a.area_name from  t_electrician  e join  t_area  a on  e.area_id=a.area_id and  e.sys_is_delete=0 
and a.network_licence=#{network_licence}	
and a.special_certificate=#{special_certificate}	
and a.professional_credential=#{professional_credential}

mapper.update:
	update t_electrician 

	sys_hash=#{sys_hash},
	area_id=#{area_id}

  where		electrician_id=#{electrician_id}
